css:
.folio-header { text-align: center; margin-bottom: 20px; }
.folio-details table { width: 100%; margin-bottom: 15px; }
.folio-details td { padding: 4px 8px; }
.folio-details .label { font-weight: bold; width: 40%; }
.folio-charges table { width: 100%; border-collapse: collapse; }
.folio-charges th, .folio-charges td { border: 1px solid #ddd; padding: 6px 8px; }
.folio-charges th { background: #f5f5f5; text-align: left; }
.folio-summary { margin-top: 15px; text-align: right; }
.folio-summary table { margin-left: auto; }
.folio-summary td { padding: 4px 12px; }
.folio-summary .total { font-weight: bold; font-size: 14px; }
