css:
.receipt-header { text-align: center; margin-bottom: 20px; }
.receipt-header h2 { margin: 0; }
.receipt-details { margin-bottom: 15px; }
.receipt-details table { width: 100%; }
.receipt-details td { padding: 4px 8px; }
.receipt-details .label { font-weight: bold; width: 40%; }
.receipt-services { margin-top: 15px; }
.receipt-services table { width: 100%; border-collapse: collapse; }
.receipt-services th, .receipt-services td { border: 1px solid #ddd; padding: 6px 8px; text-align: left; }
.receipt-services th { background: #f5f5f5; }
.receipt-total { text-align: right; margin-top: 15px; font-size: 16px; font-weight: bold; }
.receipt-footer { text-align: center; margin-top: 30px; font-size: 12px; color: #666; }
